import * as types from "../constants/ActionType";

const data = JSON.parse(localStorage.getItem("CART"));

// const initalSate = data ? data : [];

const initalSate = [
  {
    product: {
      id: 1,
      name: "Iphone 7 plus",
      image: "https://i.ibb.co/ncn8GhL/iphone-12-pro-gold-hero.jpg",
      description: "dien thoai iphone",
      price: 500,
      inventory: 10,
      raiting: 4,
    },
    quantity: 5,
  },
  {
    product: {
      id: 1,
      name: "Iphone 7 plus",
      image: "https://i.ibb.co/ncn8GhL/iphone-12-pro-gold-hero.jpg",
      description: "dien thoai iphone",
      price: 500,
      inventory: 10,
      raiting: 4,
    },
    quantity: 5,
  },
];

const carts = (state = initalSate, action) => {
  switch (action) {
    case types.ADD_TO_CART:
      return [...state];
    default:
      return [...state];
  }
};

export default carts;
