import React, { Component } from "react";

class Product extends Component {
  constructor(props) {
    super(props);
    this.Showrating = this.Showrating.bind(this);
  }
  Showrating(rating) {
    let result = [];
    for (let i = 0; i <= rating; i++) {
      result = result.push(
        <li>
          <i class="fa fa-star"></i>
        </li>
      );
    }
    return result;
  }
  render() {
    const { product } = this.props;

    return (
      <div class="col-lg-4 col-md-6 mb-r">
        <div class="card text-center card-cascade narrower">
          <div class="view overlay hm-white-slight z-depth-1">
            <img src={product.image} class="img-fluid" alt={product.name} />
            <a>
              <div class="mask waves-light waves-effect waves-light"></div>
            </a>
          </div>
          <div class="card-body">
            <h4 class="card-title">
              <strong>
                <a>{product.name}</a>
              </strong>
            </h4>
            <ul class="rating">
                <li>
                {this.Showrating(product.rating)}
                </li>
            </ul>
            <p class="card-text">{product.description}</p>
            <div class="card-footer">
              <span class="left">{product.price}$</span>
              <span class="right">
                <a
                  class="btn-floating blue-gradient"
                  data-toggle="tooltip"
                  data-placement="top"
                  title=""
                  data-original-title="Add to Cart"
                >
                  <i class="fa fa-shopping-cart"></i>
                </a>
              </span>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Product;
